mapper.select:
select o.id, o.date, o.quantity, o.order_status as status, p.id as productId, p.name as productName, p.price
from orders o
         join product p on o.product_id = p.id
where o.member_id = #{memberId}
order by o.id desc;

mapper.update:
update orders
set order_status = #{status}
where id = #{orderId}
